  >>> rollingMax([1, 2, 3, 2, 3, 4, 2])
  [1, 2, 3, 3, 3, 4, 4]
  */
const rollingMax = (numbers) => {
    let a = numbers.slice(0);
    const result = [];
    while (a.length) {
        const max = a[0];
        let i = 1;
        while (i < a.length) {
            if (max < a[i]) {
                max = a[i];
            }
            i++;
        }
        result.push(max);
        a.splice(0, 1);
    }
