 const decodeCyclic= (s)=>{
   var groups= [], groups2= []; 
   for (const group of s.split('')){ 
     if (group.length== 3){ 
       groups.push(group.slice(1)+ group[0]);
     } 
     else { 
       groups2.push(group);
     }
  }
  return groups2.join('');
}

