const correctBracketing = (brackets) => {
    let stack = [],
        openBracket,
        closeBracket,
        openBracketIndex,
        closeBracketIndex;
    for (let i = 0; i < brackets.length; i++) {
        openBracket = brackets[i];
        if (openBracket === "(" || openBracket === "{") {
            stack.push(openBracket);
        } else {
            if (stack.length === 0) {
                return false;
            }
            if (openBracket!== stack[stack.length - 1]) {
                return false;
            }
            stack.pop();
        }
        closeBracket = brackets[i + 1];
        if (closeBracket === "(" || closeBracket === "{") {
            stack.push(closeBracket);
        } else {
