                return false;
            }
            if (closeBracket!== stack[stack.length - 1]) {
                return false;
            }
            stack.pop();
        }
    }
    if (stack.length > 0) {
        return false;
    }
    return true;
}

const stringWithBalancedBrackets = (string) => {
    if (!string) {
