/*Complete the function that will take two integers and returns 
   the product of their unit digits. 
   will Assume the input will be always valid.
  Examples:
  multiply(148, 412) should return 16.
  multiply(19, 28) should return 72.
  multiply(2020, 1851) should return 0.
  multiply(14,-15) should return 20.
  */
const multiply = (a, b) => {
    const aDigits = a.toString().split('').reverse().join('');
    const bDigits = b.toString().split('').reverse().join('');
    const aLength = aDigits.length;
    const bLength = bDigits.length;

    const product = 0;
    for (let i = 0; i < aLength; i++) {
        let c = 0;
        for (let j = 0; j < bLength; j++) {
            c += aDigits[i] * bDigits[j];
            product += c % 10;
        }
        product %= 10;
    }

    return product;
};

