/* Input to this function will be a string will represent multiple groups for nested parentheses will separate by spaces. 
   For each of the group, will output the deepest level of nesting of parentheses. 
   E.g. (() ()) will maximum two levels of nesting while ((())) will have three.

  >>> parseNestedParens('(()()) ((())) () ((())()())')
  [2, 3, 1, 3]
  */
const parseNestedParens = (paren_string) => {
  let nested_parens = [];
  let level = 0;
  for(let i = 0; i < paren_string.length; i++){
    let current_char = paren_string[i];
    if(current_char === '('){
      level++;
    } else if(current_char === ')'){
      level--;
    }
    if(level === 0){
      nested_parens.push(i);
    }
  }
  return nested_parens;
};

const findEnd = (paren_string, start_pos) => {
  let end_pos = start_pos;
  for(let i = start_pos; i < paren_string.length; i++){
    let current_char = paren_string[i];
    if(current_char === '('){
      end_pos = i;
    }
  }
  return end_pos;
};

const getMaxNesting = (paren_string) => {
  let nested_parens = parseNestedParens(paren_string);
  let max_nesting = nested_parens.length;
  for(let i = 0; i < nested_parens.length; i++){
    let end_pos = findEnd(paren_string, nested_parens[i]);
    let current_depth = paren_string.substring(nested_parens[i], end_pos).length;
    if(current_depth > max_nesting){
      max_nesting = current_depth;
    }
  }
  return max_nesting;
};

const findStart = (paren_string, end_pos) => {
  let start_pos = end_pos;
  for(let i = end_pos; i >= 0; i--){
    let current_char = paren_string[i];
    if(current_char === '('){
      start_pos = i;
    }
  }
  return start_pos;
};

const getMaxDepth = (paren_string) => {
  let nested_parens = parseNestedParens(paren_string);
  let max_depth = 0;
  for(let i = 0; i < nested_parens.length; i++){
    let start_pos = findStart(paren_string, nested_parens[i]);
    let current_depth = paren_string.substring(start_pos, nested_parens[i]).length;
    if(current_depth > max_depth){
      max_depth = current_depth;
    }
  }
  return max_depth;
};

const validateParenthesis = (paren_string) => {
  let nested_parens = parseNestedParens(paren_string);
  let max_depth = getMaxDepth(paren_string);
  let max_nesting = getMaxNesting(paren_string);
