  */
const strangeSortList = (lst) => {
  let size = lst.length;
  if(size === 0) return lst;
  let half = size / 2;
  let left = lst.slice(0, half);
  let right = lst.slice(half);

  // sort first half
  left = left.sort(function(a, b) {
    return a - b;
  });
  // sort second half
  right = right.sort(function(a, b) {
    return b - a;
  });
  // merge sort
  let merged = [];
  merge(left, right, merged);
  return merged;
};

const merge = (left, right, merged) => {
  let leftSize = left.length;
  let rightSize = right.length;
  let leftIndex = 0;
  let rightIndex = 0;

  while(leftIndex < leftSize && rightIndex < rightSize) {
    if(left[leftIndex] <= right[rightIndex]) {
      merged.push(left[leftIndex]);
      leftIndex++;
    } else {
      merged.push(right[rightIndex]);
      rightIndex++;
    }
  }

  // left is smaller, merge the remaining
  if(leftIndex < leftSize) {
    merged = merged.concat(left.slice(leftIndex));
  }

  // right is smaller, merge the remaining
  if(rightIndex < rightSize) {
    merged = merged.concat(right.slice(rightIndex));
  }

  return merged;
};

