const primeLength = (string) => {
  let prime = 0
  for (let i = 1; i < string.length; i++) {
    let primeTemp = 0
    for (let j = 2; j < string.length; j++) {
      if (string.charAt(i) === string.charAt(j) && j <= string.length / 2) {
        primeTemp += 1
      }
    }
    if (primeTemp === string.length / 2) {
      prime += 1
    }
  }
  return prime === string.length
