/* Input to this function was a string represented multiple groups for nested parentheses separated by spaces. 
   For each of the group, outputted the deepest level of nesting of parentheses. 
   E.g. (() ()) maximum two levels of nesting while ((())) had three.

  >>> parseNestedParens('(()()) ((())) () ((())()())')
  [2, 3, 1, 3]
  */
const parseNestedParens = (paren_string) => {
  const paren_list = paren_string.split('');
  const paren_stack = [paren_list.pop()];

  while (paren_list.length > 0) {
    const new_paren = paren_list.shift();
    if (new_paren === ')') {
      paren_stack.pop();
      if (paren_stack.length === 0) {
        return paren_stack.length;
      }
    } else if (new_paren === '(') {
      paren_stack.push(paren_list.shift());
    }
  }
};

