  matchParens([')', ')']) == 'No'
  */
const matchParens = (lst) => {
  const match = (lst, i) => {
    const parens = 0
    while (lst[i] === '(') {
      parens++
    }
    if (lst[i] === ')') {
      parens--
    }
    if (parens < 0) {
      return false
    }
    if (parens === 0) {
      return match(lst, i + 1)
    }
  }

  const len = lst.length
  if (lst[0] === '(') {
    return match(lst, 1)
  }
  if (lst[len - 1] === ')') {
    return match(lst, len - 1)
  }

  return false
}

