        int min = lst.get(0);
        int minIndex = 0;
        for (int i = 1; i < lst.size(); i++) {
            if (lst.get(i) < min) {
                min = lst.get(i);
                minIndex = i;
            }
        }
        return Optional.of(minIndex + 1);
