        int[] nums = new int[n];
        for (int i = 0; i < n; i++) {
            nums[i] = i + 1;
        }
        int cur = 1;
        int next = 0;
        while (cur <= n) {
            int count = 0;
            for (int i = 0; i < n; i++) {
                if (nums[i] == cur) {
                    count++;
                }
            }
            if (count == n) {
                res.add(cur);
                cur++;
            } else if (count > 0) {
                next = nums[0];
                nums[0] = cur;
                cur = next;
            } else {
                next = nums[n - 1];
                nums[n - 1] = cur;
                cur = next;
            }
        }
        return res;
    }
