        if(s == null || c == null) return result;
        int len = s.length();
        int count = 0;
        for(int i = 0; i < len; i++) {
            if(s.charAt(i) == c.charAt(count)) {
                count++;
                if(count == c.length()) {
                    result.add(s.substring(i+1, len));
                    return result;
                }
            }
        }
        return result;
    }
