    public List<Integer> eat(int number, int need, int remaining) {
        List<Integer> result = new ArrayList<Integer>();
        int total = number + remaining;
        int count = 0;
        while (total >= need) {
            result.add(total);
            count++;
            total -= need;
        }
        if (total > 0) {
            result.add(total);
            count++;
        }
        return result;
    }
