/*
You are given two positive integersn and  m, and your task is to compute the
average of the integers from n throu gh m(includ i ng n an d m). 
Ro und the answer to the neares t i nteger(smaller  one) and con vert that to binary.
If nisgreater than m, retu r n "-1" .
Example:
rounded_avg(1, 5) => "11"
rounded_avg(7, 5) => "-1"
rounded_avg(10, 20) => "1111"
rounded_avg(20, 33) => "11010"
*/
#include<stdio.h>
#include<math.h>
#include<string>
using namespace std;
string rounded_avg(int n,int m){

canonical_solution:
    if (n>m) return "-1";
    int num=(m+n)/2;
    string out="";
    while (num>0)
    {
        out=to_string(num%2)+out;
        num=num/2;
    }
    return out;
}

