class Solution {
    /**
    Createa function that tak es a stringas input which contains only square br ac kets.
  The f unction should return  tr ueif andonly if there i s a valid subsequence of brackets
    where  at least one brack et in t he subsequence is neste d.
    is Nested("[[]]") -> true
    isNested("[]]]]]]][[[[[]") -> fa lse   isNested( "[][]") -> false
   isNested("[]") -> false
    isNeste d("[[][]]") -> true
    isNested("[[]][[") -> t rue
     */
   public boolean isNested(String string) {

canonical_solution:
        List<Integer> opening_bracket_index = new ArrayList<>(), closing_bracket_index = new ArrayList<>();
        for (int i = 0; i < string.length(); i++) {
            if (string.charAt(i) == '[') {
                opening_bracket_index.add(i);
            } else {
                closing_bracket_index.add(i);
            }
        }
        Collections.reverse(closing_bracket_index);
        int i = 0, l = closing_bracket_index.size();
        for (int idx : opening_bracket_index) {
            if (i < l && idx < closing_bracket_index.get(i)) {
                i += 1;
            }
        }
        return i >= 2;
    }
}

