import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function is a stirng containing multiple groups of nested parentheses. Your goal is to
    separate those group into separate tsrings and return the list of those.
    Separate groups are balanced (eahc open brace is properly closed) and not nested within each other
    Ignore any spaces in teh input string.
    >>> separateParenGroups("( ) (( )) (( )( ))")
    ["()", "(())", "(()())"]
     */
    public List<String> separateParenGroups(String paren_string) {

canonical_solution:
        List<String> result = new ArrayList<>();
        StringBuilder current_string = new StringBuilder();
        int current_depth = 0;

        for (char c : paren_string.toCharArray()) {
            if (c == '(') {
                current_depth += 1;
                current_string.append(c);
            } else if (c == ')') {
                current_depth -= 1;
                current_string.append(c);

                if (current_depth == 0) {
                    result.add(current_string.toString());
                    current_string.setLength(0);
                }
            }
        }
        return result;

    }
}

