/*
Create a function which returns the largest index of an element which
is not greater than or equal to the element immediately preceding it. If
no such element exists then return -1. The given vector will not contain
duplicate values.

Examples:
can_arranging({1,2,4,3,5}) = 3
can_arranging({1,2,3}) = -1
*/
#include<stdio.h>
#include<vector>
using namespace std;
int can_arranging(vector<int> arr){
    int n = arr.size();
    if(n == 0) return -1;
    int i = 0;
    int j = n-1;
    while(i < j){
        if(arr[i] > arr[j]) return i;
        if(arr[i] < arr[j]) return -1;
        i++;
        j--;
    }
    return i;
}

int main(){
    vector<int> arr = {1,2,4,3,5};
    printf("%d\n", can_arranging(arr));
    return 0;
}
