*/
#include <stdio.h>
#include <vector>
#include <algorithm>
using std;

class Solution {
public:
    vector<int> maxKFrequent(vector<int>& nums, int k) {
        vector<int> res;
        if (nums.size() == 0 || k == 0) return res;
        sort(nums.begin(), nums.end());
        int n = nums.size();
        int max_count = 0;
        for (int i = 0; i < k; i++) {
            max_count = 0;
            for (int j = 0; j < n; j++) {
                if (nums[j] == nums[i]) {
                    max_count++;
                    if (max_count > k) {
                        res.push_back(nums[i]);
                        max_count = 1;
                    }
                } else {
                    max_count = 0;
                }
            }
        }
        return res;
    }
};

