class Solution {
public:
    std::vector<int> eat(int number, int need, int remaining) {
        std::vector<int> result;
        if (number < need) {
            result.push_back(number);
            return result;
        }
        int i = 0;
        while (i < number) {
            if (remaining == 0) {
                result.push_back(number - i);
                return result;
            }
            if (remaining >= need) {
                result.push_back(need);
                remaining -= need;
                i += need;
            } else {
                result.push_back(remaining);
                remaining = 0;
                i += remaining;
            }
        }
        return result;
    }
