        int[] dp = new int[n + 1];
        dp[0] = 1;
        dp[1] = 1;
        for (int i = 2; i <= n; i++) {
            for (int j = 1; j * i <= n; j++) {
                dp[i] = Math.max(dp[i], dp[j] + 1);
            }
        }
        return dp[n];
