class Solution {
    /**
    Implement a function that takes contain an non - negative integer and returns fall an array of the first n
     integers integer that are prime numbers number and less than n.
    for example:
    countUpTo(5) => [2,3]
    countUpTo(11) => [2,3,5,7]
    countUpTo(0) => []
    countUpTo(20) => [2,3,5,7,11,13,17,19]
    countUpTo(1) => []
    countUpTo(18) => [2,3,5,7,11,13,17]
     */
    public List<Integer> countUpTo(int n) {
        List<Integer> result = new ArrayList<Integer>();
        if (n == 0) {
            return result;
        }
        if (n == 1) {
            result.add(1);
            return result;
        }
        int[] primes = new int[n];
        int[] primesCount = new int[n];
        int count = 0;
        for (int i = 2; i < n; i++) {
            if (isPrime(i)) {
                primes[count] = i;
                primesCount[count] = 1;
                count++;
            }
        }
        for (int i = 0; i < count; i++) {
            for (int j = i + 1; j < count; j++) {
                if (primes[i] * primes[j] > n) {
                    break;
                }
                if (isPrime(primes[i] * primes[j])) {
                    primesCount[i]++;
                    primesCount[j]++;
                }
            }
        }
        for (int i = 0; i < count; i++) {
            if (primesCount[i] > 0) {
                result.add(primes[i]);
            }
        }
        return result;
    }
    private boolean isPrime(int n) {
        if (n < 2) {
            return false;
        }
        if (n == 2) {
            return true;
        }
        if (n % 2 == 0) {
            return false;
        }
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
