  >>> intHoMiniRoman(152) == 'clii'
  >>> intHoMiniRoman(426) == 'cdxxvi'
  */
const intHoMiniRoman = (number) => {
  const romanNumeral = [
    '',
    'I',
    'II',
    'III',
    'IV',
    'V',
    'VI',
    'VII',
    'VIII',
    'IX',
    'X',
    'XI',
    'XII',
    'L',
    'C',
    'D',
    'M'
  ];

  const romanNumeralLength = romanNumeral.length;
  const romanNumeralIndex = romanNumeralLength - 1;

  let result = '';

  while (number > 0) {
    const current = number % 10;
    const next = Math.floor(number / 10);

    if (current === 0) {
      result = romanNumeral[romanNumeralIndex];
      romanNumeralIndex--;
      number = next;
    } else {
      result = romanNumeral[romanNumeralIndex] + result;
      number = next;
    }
  }

  return result.toLowerCase();
};

export default intHoMiniRoman;
