  For example:
  countLetters('aBCdEf') returns 1
  countLetters('abcdefg') returns 0
  countLetters('dBBE') returns 1
  */
const countLettersOddEven = (s) => {
  let count = 0;
  for (let i = 0; i < s.length; i++) {
    if (s[i] >= 'a' && s[i] <= 'z') {
      count++;
    }
  }
  return count % 2 === 1? count : 0;
};

