  Rounding away from zero means that if the given number is equidistant
  from two integers, the one you should return is the one that is the
  farthest from zero. For example closestInteger("14.5") should
  return 15 and closestInteger("-14.5") should return -15.
  */
const closestInteger = (value) => {
  let closest = value;
  let diff = Math.abs(value - closest);
  let diff2 = Math.abs(value - closest);
  let diff3 = Math.abs(value - closest);
  if (diff < diff2 && diff < diff3) {
    closest = value;
  } else if (diff2 < diff && diff2 < diff3) {
    closest = value;
  } else if (diff3 < diff && diff3 < diff2) {
    closest = value;
  }
  return closest;
};

module.exports = {
  closestInteger
};
