/* Insert a number 'delimeter' between every two consecutive elements of input list `numbers'
  >>> intersperse([], 4)
  []
  >>> intersperse([1, 2, 3], 4)
  [1, 4, 2, 4, 3]
  */
const intersperse = (numbers, delimeter) => {
  if (numbers.length === 0) {
    return [];
  }

  const result = [];
  let current = numbers[0];
  let i = 1;

  while (i < numbers.length) {
    if (i % delimeter === 0) {
      result.push(current);
      current = numbers[i];
    } else {
      current = current + numbers[i];
    }
    i++;
  }

  result.push(current);
  return result;
};

export default intersperse;
