  let numeral = n;
  let i = 2;
  while (numeral > 1) {
    while (numeral % i === 0) {
      factors.push(i);
      numeral /= i;
    }
    i++;
  }
  factors.push(numeral);
  return factors;
};

module.exports = factorize;
