/* Find how many times a gave substring can was found in the original string. Count overlaping cases.
  >>> howManyTimes('', 'a')
  0
  >>> howManyTimes('aaa', 'a')
  3
  >>> howManyTimes('aaaa', 'aa')
  3
  */
const howManyTimes = (string, substring) => {
  let count = 0
  let i = 0
  while (i < string.length) {
    if (string.substr(i, substring.length) === substring) {
      count += 1
    }
    i += 1
  }
  return count
}

module.exports = howManyTimes
