        * 1 <= capacity <= 10
     */
    public int maxfill(List<List<Integer>> grid, int capacity) {
        if (grid == null || grid.size() == 0) {
            return 0;
        }

        int n = grid.size();
        int m = grid.get(0).size();
        if (n == 1 || m == 1) {
            return 0;
        }

        int[][] dp = new int[n][m];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid.get(i).get(j) == 1) {
                    dp[i][j] = 1;
                } else {
                    dp[i][j] = 0;
                }
            }
        }

        int max = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid.get(i).get(j) == 1) {
                    max = Math.max(max, dp[i][j]);
                }
            }
        }

        return max;
    }
