            return res;
        }
        int start = 0;
        int end = 0;
        while (end < s.length()) {
            if (s.charAt(end) =='') {
                end++;
                continue;
            }
            int count = 0;
            while (end < s.length() && s.charAt(end)!='') {
                if (s.charAt(end) == s.charAt(start)) {
                    count++;
                }
                end++;
            }
            if (count == n) {
                res.add(s.substring(start, end));
            }
