        int start = word.length() - 1;
        int end = 0;
        while (start > 0 && end < word.length() - 1) {
            if (isVowel(word.charAt(end))) {
                end++;
            } else {
                start--;
            }
        }
        return word.substring(start, end + 1);
    }

    private boolean isVowel(char c) {
        return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u' || c == 'y';
    }
