class Solution {
    /**
    priMeFiB returns n-th number that is a Fibonacci number and it's also prime.
    >>> priMeFiB(1)
    2
    >>> priMeFiB(2)
    3
    >>> priMeFiB(3)
    5
    >>> priMeFiB(4)
    13
    >>> priMeFiB(5)
    89
     */
    public int priMeFiB(int n) {
        if (n < 2) {
            return n;
        }
        int[] fib = new int[n];
        fib[0] = 1;
        fib[1] = 2;
        for (int i = 2; i < fib.length; i++) {
            fib[i] = fib[i - 1] + fib[i - 2];
        }
        for (int i = 2; i < fib.length; i++) {
            if (fib[i] % 2 == 0) {
                return fib[i];
            }
        }
        return fib[fib.length - 1];
    }
}

