        if (a < 0 || h < 0) {
            return 0;
        }
        if (a == 0 || h == 0) {
            return 0;
        }
        if (a == h) {
            return a;
        }
        if (a > h) {
            area = (a * h) / 2;
        } else {
            area = (h * a) / 2;
        }
        return area;
