    path[i] = 0;
  }

  for (let i = 0; i < n; i++) {
    for (let j = 0; j < m; j++) {
      if (visited[i * m + j] === 0) {
        const min = i + 1;
        const max = i + m - 1;
        const min_j = j + 1;
        const max_j = j + m - 1;

