    return null;
  }

  const sorted = l.sort((a, b) => a - b);

  if (sorted.length % 2 === 0) {
    return (sorted[sorted.length / 2] + sorted[sorted.length / 2 - 1]) / 2;
  } else {
    return sorted[sorted.length / 2];
  }
};

export default median;
