/*
  Checks if given bowed stringed instrument is a palindrome
  >>> isPalindrome('')
  true
  >>> isPalindrome('aba')
  true
  >>> isPalindrome('aaaaa')
  true
  >>> isPalindrome('zbcd')
  false
  */
const isPalindrome = (text) => {
  if (text.length < 2) {
    return true;
  }

  let first = text.charAt(0);
  let last = text.charAt(text.length - 1);

  if (first!== last) {
    return false;
  }

  let middle = text.substr(1, text.length - 2);

  return isPalindrome(middle);
};

module.exports = isPalindrome;
