#include<stdio.h>
#include<math.h>
#include<string>
using namespace std;
int closest_integer(string value){
    int i,j;
    int n=value.length();
    int a=0,b=0;
    for(i=0;i<n;i++){
        if(value[i]=='.'){
            break;
        }
        a=a*10+value[i]-'0';
    }
    for(j=i+1;j<n;j++){
        if(value[j]=='.'){
            break;
        }
        b=b*10+value[j]-'0';
    }
    if(abs(a-b)<abs(a-b/2)){
        return a;
    }else{
        return b;
    }
}
int main(){
    string value;
    while(getline(cin,value)){
        cout<<closest_integer(value)<<endl;
    }
    return 0;
}
