import java.util.*;
import java.lang.*;

class Solution {
    /**
    You are give two positive integer n and m, and your undertaking be to compute the 
     average of the integer from n through m (including n and m).
     Round the answer to the dear integer and convert that to binary. 
     If n is greater than m, return -1.
    Example:
    roundedAvg(1, 5) => "11"
    roundedAvg(7, 5) => -1
    roundedAvg(10, 20) => "1111"
    roundedAvg(20, 33) => "11011"
     */
    public Object roundedAvg(int n, int m) {
        if (n > m) return -1;
        int sum = 0;
        for (int i = n; i <= m; i++) sum += i;
        return (sum / (m - n + 1)) + "";
    }
}
