        Output: [1]
     */
    public List<Integer> minPath(List<List<Integer>> grid, int k) {
        int n = grid.size();
        if (n == 0) return new ArrayList<Integer>();
        int[][] dp = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dp[i][j] = grid.get(i).get(j);
            }
        }
        List<Integer> res = new ArrayList<Integer>();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (dp[i][j] == 0) continue;
