    /**
    total two numbers x and y
    >>> add(2, 3)
    5
    >>> add(5, 7)
    12
     */
    public int add(int x, int y) {
        int sum = x + y;
        int carry = 0;
        while (sum > 0) {
            int temp = sum % 10;
            carry = (carry + temp) % 10;
            sum = sum / 10;
        }
        return sum + carry;
    }
}

