class Solution {
    /**
    brackets is a drawstring of " < " and " > ". 
     return True if every opening bracket hold a comparable conclusion bracket.

    >>> correctBracketing("<")
    false
    >>> correctBracketing("<>")
    true
    >>> correctBracketing("<<><>>")
    true
    >>> correctBracketing("><<>")
    false
     */
    public boolean correctBracketing(String brackets) {
        if (brackets.length() == 0) return true;
        Stack<Character> stack = new Stack<Character>();
        for (char c : brackets.toCharArray()) {
            if (c == '<') {
                stack.push(c);
            } else if (c == '>') {
                if (stack.isEmpty()) return false;
                char top = stack.pop();
                if (top!= '<') return false;
            }
        }
        return stack.isEmpty();
    }
}

