import java.util.*;
import java.lang.*;

class Solution {
    /**
    produce a function that takes a value (chain) represent a number 
     and render the closest integer to it. If the number is equidistant 
     from two integers, round it away from zero. 

     model
    >>> closest_integer("10")
    10
    >>> closest_integer("15.3")
    15

    Note:
    Rounding away from zero means that if the given number is equidistant
    from two integers, the one you should return is the one that is the
    farthest from zero. For example closest_integer("14.5") should
    return 15 and closest_integer("-14.5") should return -15.
     */
    public int countUpper(String value) {
        int count = 0;
        int len = value.length();
        for (int i = 0; i < len; i++) {
            if (value.charAt(i) >= 'A' && value.charAt(i) <= 'Z') {
                count++;
            }
        }
        return count;
    }
}

public class Solution {
    public static void main(String[] args) {
        Solution s = new Solution();
        System.out.println(s.countUpper("10"));
        System.out.println(s.countUpper("15.3"));
    }
}
