import java.util.*;
import java.lang.*;

class Solution {
    /**
    Thisfunction takes two positive numbers x and y and returns the
   bigges t even  integer number thatis in the  range [x, y ] incl usive.If
    there's no such number,  then the fu nc tion should ret u rn -1.

    For example:
    chooseNum(12, 15) = 14
    chooseNum(13, 12) = -1
     */
    public int chooseNum(int x, int y) {
        if (x == y) return x;
        if (x < y) {
            return chooseNum(y, x);
        }
        int max = x;
        int min = y;
        int mid = (max + min) / 2;
        int count = 0;
        while (max >= mid) {
            count++;
            max = mid;
            mid = (max + min) / 2;
        }
        if (count == 0) return -1;
        return mid;
    }
}

public class Solution {
    public static void main(String[] args) {
        Solution s = new Solution();
        System.out.println(s.chooseNum(12, 15));
        System.out.println(s.chooseNum(13, 12));
