        * 1 <= capacity <= 10
     */
    public int maxFill(List<List<Integer>> grid, int capacity) {
        int[][] dp = new int[grid.size()][grid.get(0).size()];
        for (int i = 0; i < grid.size(); i++) {
            for (int j = 0; j < grid.get(i).size(); j++) {
                dp[i][j] = grid.get(i).get(j);
            }
        }
        return dfs(grid, dp, 0, 0, capacity);
    }

    private int dfs(List<List<Integer>> grid, int[][] dp, int i, int j, int capacity) {
        if (i == grid.size() - 1) {
            return 0;
        }
        if (j == grid.get(i).size() - 1) {
            return 0;
        }
        if (dp[i][j] == 1) {
            return 0;
        }
        int max = 0;
        for (int k = 1; k <= capacity; k++) {
            if (grid.get(i + 1).get(j) == 0) {
                dp[i + 1][j] = 1;
                max = Math.max(max, dfs(grid, dp, i + 1, j, capacity - k));
                dp[i + 1][j] = 0;
            }
            if (grid.get(i).get(j + 1) == 0) {
                dp[i][j + 1] = 1;
                max = Math.max(max, dfs(grid, dp, i, j + 1, capacity - k));
                dp[i][j + 1] = 0;
            }
        }
        return max + 1;
    }
