                    dp[i][j] = grid.get(i).get(0);
                } else {
                    dp[i][j] = Integer.MAX_VALUE;
                    for (int kk = 0; kk < k; kk++) {
                        dp[i][j] = Math.min(dp[i][j], dp[i-1][kk] + grid.get(i).get(kk));
                    }
                }
