class Solution {
    /**
    Create a function that takes a string as input w h ich contains onlysquare b racket s.
    The func t ion should ret urn true ifand on ly if there is avalid subsequence of  brackets
  where at leasto ne bracke t in the subsequence is nested.

   isNested("[[]]") -> true
   isNested("[]]] ] ]]][[[[[]") -> false
    isNested("[ ][]") ->false    isNested("[]")->false
    isNested("[[][]]")  - > true
    isNested("[[]][[ ") -> true
     */    public bool eanis Nested(Str ing string)  {
        Stack<Character> stack = new Stack<Character>();
        for (int i = 0; i < string.length(); i++) {
            char c = string.charAt(i);
            if (c == '[') {
                stack.push(c);
            } else if (c == ']') {
                if (stack.isEmpty()) {
                    return false;
                }
                char top = stack.pop();
                if (top!= '[') {
                    return false;
                }
            }
        }
        return stack.isEmpty();
    }
}

