        StringBuilder sb = new StringBuilder();
        String[] words = sentence.split(" ");
        int[] primes = new int[words.length];
        for (int i = 0; i < words.length; i++) {
            primes[i] = isPrime(words[i].length());
        }
        Arrays.sort(primes);
        for (int i = 0; i < words.length; i++) {
            sb.append(words[i]).append(" ");
        }
        return sb.toString();
    }

    private int isPrime(int n) {
        if (n <= 1) return 0;
        for (int i = 2; i * i <= n; i++) {
            if (n % i == 0) return 0;
        }
        return 1;
    }
