  for (let i = 1; i <= n; i++) {
    let start = i;
    let end = i;
    while (start > 0) {
      if (start % 10 === 1) {
        count++;
      }
      start = start / 10;
    }
    while (end < n) {
      if (end % 10 === 1) {
        count++;
      }
      end = end / 10;
    }
  }
  return count;
};

