      y: Math.random() * 100,
      vx: Math.random() * 2 - 1,
      vy: Math.random() * 2 - 1,
      r: Math.random() * 100,
    };
  });

  const carsLeft = cars.slice(0, n / 2);
  const carsRight = cars.slice(n / 2, n);

  const carsLeftCollisions = carsLeft.map((car) => {
    const carRight = carsRight.find((rightCar) => {
      return car.x === rightCar.x && car.y === rightCar.y;
    });

    if (carRight) {
      return {
        car: car,
        rightCar: carRight,
      };
    }

    return null;
  });

  const carsRightCollisions = carsRight.map((car) => {
    const carLeft = carsLeft.find((leftCar) => {
      return car.x === leftCar.x && car.y === leftCar.y;
    });

    if (carLeft) {
      return {
        car: car,
        leftCar: carLeft,
      };
    }

    return null;
  });

  const collisions = carsLeftCollisions.concat(carsRightCollisions);

  return collisions.length;
};

