  const consonants = ["b", "c", "d", "f", "g", "h", "j", "k", "l", "m", "n", "p", "q", "r", "s", "t", "v", "w", "x", "y", "z"]
  const closestVowel = ""
  const closestConsonant = ""
  const closestDistance = 0
  const closestVowelIndex = 0
  const closestConsonantIndex = 0
  const wordLength = word.length
  for (let i = 0; i < wordLength; i++) {
    if (vowels.indexOf(word[i])!== -1) {
      if (closestVowel === "" || closestDistance > i - closestVowelIndex) {
        closestVowel = word[i]
        closestVowelIndex = i
        closestDistance = i - closestConsonantIndex
        closestConsonantIndex = i
      }
    }
    if (consonants.indexOf(word[i])!== -1) {
      if (closestVowel === "" || closestDistance > i - closestVowelIndex) {
        closestVowel = ""
        closestDistance = 0
      }
    }
  }
  return closestVowel
}

